mapper.select:
SELECT COUNT(*)
FROM board_join_me
WHERE region = #{keyword}

SELECT *
FROM board_join_me AS b
         JOIN user AS u ON b.user_id = u.user_id
WHERE region = #{keyword}
ORDER BY join_me_id DESC LIMIT #{startNum}, #{pageSize}

mapper.update:
UPDATE board_join_me
SET title=#{title},
    content=#{content},
    start_date=#{startDate},
    end_date=#{endDate},
    region=#{region},
    charge=#{charge},
    capacity=#{capacity}
WHERE join_me_id = #{joinMeId}
  AND user_id = #{userId}

UPDATE board_join_me
SET view= view +1
WHERE join_me_id=#{joinMeId}

UPDATE board_join_me
SET closed=#{closed}
WHERE join_me_id = #{joinMeId}

mapper.delete:
DELETE
FROM board_join_me
WHERE join_me_id = #{joinMeId}
  AND user_id = #{userId}
